require 'mecab'
require 'uri'
module ZchSentence
  public

  def self.mecab=(mecab)
    @@mecab = mecab
  end

  # 「。」「、」を付加するなどの自動校正を行う
  def self.emend(body)
    adhoc_split(body).join("\n")
  end

  #   ２ちゃんねるのレス（生データ）を想定したさまざまなルールを使って
  #   文単位に切って返す。
  #   「。」などを勝手に補完しながら処理するので元の文字列とは違うことがある。
  #   各分の文末は「。」や「？」などが残る。
  def self.adhoc_split(body)
    lines = split_eos(body)
    # 最後と空改行を挟む場合は文末
    # 空行を削除
    new_lines = []
    lines.each_with_index do |line, i|
      if (!EOS_REGEXP.match(line))
        if (i == lines.size - 1)
          line = put_mark(line, "。")
        elsif (lines[i + 1].empty?)
          line = put_mark(line, "。")
        end
      end
      unless (line.empty?)
        new_lines << line
      end
    end
    lines = new_lines
    eos_count = lines.reduce(0){|count, line| count + (EOS_REGEXP.match(line) ? 1 : 0) }
    if (lines == eos_count)
      # 句点等がきちんとしてる
      lines
    else
      # 文の途中が改行で区切られてる
      new_lines = []
      lines.each_index do |i|
        line = lines[i]
        next_line = lines[i + 1]
        unless (next_line)
           new_lines << put_mark(line, "。")
           break
        end
        if (!EOS_REGEXP.match(line))
          if (EOC_REGEXP.match(line))
            # 「、」など文末でないことが明示されている場合は次の行へ移す
            if (next_line =~ /^[\x00-\x7f]+$/)
              # ..
            else
              lines[i + 1] = line + lines[i + 1]
              line = nil
            end
          elsif (line =~ /^[\x00-\x7f]+$/)
            # 半角のみ(URLなど)はそのまま
          else
            ct = parse_token(line)
            nt = parse_token(next_line)
            if (ct.size >= LEFT_TOKENS && nt.size >= RIGHT_TOKENS)
              # ルールで判定できない場合は機械学習器で文末か判定
              if (predict(ct[ct.size - LEFT_TOKENS, LEFT_TOKENS] + nt[0, RIGHT_TOKENS]))
                # 文末ということに
                line = put_mark(line, "。")
              else
                # 文末でない場合は次の行へ移す
                if (next_line =~ /^[\x00-\x7f]+$/)
                  # ただし次の行がURLなどの場合はここで止める
                  line = put_mark(line, "。")
                else
                  lines[i + 1] = put_mark(line, "、") + lines[i + 1]
                  line = nil
                end
              end
            else
              # どちらかが短い場合は(改行する必要がないと思うので)文末っぽい
              line = put_mark(line, "。")
            end
          end
        end
        unless (line.nil? || line.empty?)
          new_lines << line
        end
      end
      new_lines
    end
  end

  # 機械学習器だけで文単位に切って返す
  # 「。」や「、」「？」などはあらかじめ消さないとうまく動かないかも
  # あまり精度よくない……
  def self.split(s)
    sentences = []
    tokens = parse_token(s)
    s = ""
    i = 0
    while i < tokens.size - TOKENS
      if (predict(tokens[i, TOKENS]))
        s << tokens[i, LEFT_TOKENS].map{|n| n.surface}.join
        sentences << s
        s = ""
        i += LEFT_TOKENS
      else
        s << tokens[i].surface
        i += 1
      end
    end
    while i < tokens.size
      s << tokens[i].surface
      i += 1
    end
    unless (s.empty?)
      sentences << s
    end
    sentences
  end

  # 学習データ作成
  # type = 1 「。」で区切るか(label =1) 「。」で区切らないか(label = 2)のデータを作成
  # type = 2 「。」で区切るか(label =1) 「、」で区切るか(label = 2)のデータを作成
  #
  # たとえばLIBLINEAR用はこんな感じ (id_dic.get_idは文字列をIDに変換する処理)
  #
  # ZchSentence.make_train_data(1, text) do | label, vec |
  #   puts "#{label} " + vec.map{|word| id_dic.get_id(word)}.sort.map{|i| "#{i}:1"}.join(" ")
  # end
  def self.make_train_data(type, text, &block)
    if (type != 1 && type != 2)
      raise ArgumentError.new("type = 1 or 2")
    end

    text1 = normalize_train_data(text, true)
    text2 = normalize_train_data(text, false)

    [0, 1].each do |k|
      if (type == 1 && k == 1)
        break
      end
      if (k == 0)
        lines = text1.split('。')
      else
        lines = text2.split('、')
      end
      lines.each_with_index do |line, i|
        if (i >= lines.size - 1)
          break
        end
        r_line = lines[i + 1]
        r_nodes = parse_token(r_line)
        r_tokens = r_nodes[0 ... RIGHT_TOKENS]
        if (r_tokens.size == RIGHT_TOKENS && r_nodes.size > RIGHT_TOKENS + 2)
          l_tokens = parse_token(line)
          if (l_tokens.size > LEFT_TOKENS + RIGHT_TOKENS)
            if (type == 1)
              # 「。」区切りか区切りなしかの分類
              # posi
              block.call(1, feature_vec(l_tokens[l_tokens.size - LEFT_TOKENS .. -1] + r_tokens))
              # nega
              (1 ... [LEFT_TOKENS, RIGHT_TOKENS].min).each do |o|
                block.call(2, feature_vec(l_tokens[l_tokens.size - LEFT_TOKENS - o .. - 1] + r_nodes[0 ... RIGHT_TOKENS - o]))
                block.call(2, feature_vec(l_tokens[l_tokens.size - LEFT_TOKENS + o .. - 1] + r_nodes[0 ... RIGHT_TOKENS + o]))
              end
              (1 .. (l_tokens.size - TOKENS + 1)).each do |i|
                block.call(2, feature_vec(l_tokens[l_tokens.size - (i + TOKENS - 1) .. -i]))
              end
            else
              # 「、」区切りか「。」区切りかの分類
              if (k == 0)
                block.call(1, feature_vec(l_tokens[l_tokens.size - LEFT_TOKENS .. -1] + r_tokens))
              else
                block.call(2, feature_vec(l_tokens[l_tokens.size - LEFT_TOKENS .. -1] + r_tokens))
              end
            end
          end
        end
      end
    end
  end

  private

  LEFT_TOKENS = 3
  RIGHT_TOKENS = 3
  TOKENS = LEFT_TOKENS + RIGHT_TOKENS
  EOS_REGEXP = /[。？\?！!．]+$/
  EOC_REGEXP = /[、，]$/

  def self.mecab
    @@mecab ||= MeCab::Tagger.new
  end
  class Token
    attr_accessor :surface, :feature, :char_type

    def self.u(s)
      RUBY_VERSION >= "1.9" ? s.force_encoding("utf-8") : s
    end
    def self.char_type(s)
      if (s =~ /^[一-龠]+$/)
        '1'
      elsif (s =~ /^[ぁ-ん]+$/)
        '2'
      elsif (s =~ /^[ァ-ヴー・]+$/)
        '3'
      else
        '4'
      end
    end
    def initialize(node)
      @surface = Token.u(node.surface)
      @feature = Token.u(node.feature).split(",")[0 ... 2]
      @char_type = Token.char_type(@surface)
    end
  end
  def self.parse_token(s)
    if (s.is_a?(String))
      node = mecab.parseToNode(s)
    else
      node = s
    end
    tokens = []
    while node
      tokens << Token.new(node)
      node = node.next
    end
    tokens = tokens[1 ... -1]
    if (tokens)
      tokens
    else
      []
    end
  end
  def self.vec_key(word, type)
    word + '_' + type
  end
  def self.feature_vec(tokens)
    if (tokens.size != TOKENS)
      raise tokens.map{|n| n.surface }.join(",")
    end
    # p tokens.map{|t| t.surface}
    [1, 2, 3].map do |k|
      (0 .. (TOKENS - k)).map do |i|
        nword = tokens[i, k]
        [vec_key(nword.map{|n| n.surface }.join, "N#{k}-S-C#{i}"),
         vec_key(nword.map {|n| n.feature.join(",") }.join("|"),
                 "N#{k}-F-C#{i}"),
         vec_key(nword.map{|n| [ n.surface, n.feature.join(",")].
                   join("-") }.join("|"), "N#{k}-SF-C#{i}"),
         vec_key(nword.map{|n| n.char_type }.join(","), "N#{k}-C-C#{i}")
        ]
      end
    end.flatten.uniq
  end
  def self.put_mark(s, mark)
    if (s =~ /^[\x00-\x7f]+$/)
      s
    elsif (s =~ /[一-龠ぁ-んァ-ヴ]$/)
      s + mark
    else
      s
    end
  end
  def self.strip(s)
    s.gsub(/^[\s　]+/, '').gsub(/[\s　]+$/, '')
  end
  def self.split_eos(body)
    body.split(/\n/).map do |line|
      lines = []
      s = ""
      line = strip(line)
      if (URI.regexp(['http','ttp','tp','https','ttps','tps']).match(line))
        lines << line
      else
        b = false
        eos = ""
        line.chars.each do |c|
          if (EOS_REGEXP.match(c))
            b = true
            eos << c
          elsif (b)
            lines << strip(s + eos)
            eos = ""
            s = c
            b = false
          else
            s += c
            b = false
          end
        end
        lines << strip(s + eos)
      end
      lines
    end.flatten
  end
  def self.predict(tokens)
    unless (tokens.size == TOKENS)
      raise ArgumentError.new("invalid node size.")
    end
    if (feature_vec(tokens).reduce(0.0) {|z, word| z + (@@dic1[word] || 0)} > 0.0)
      feature_vec(tokens).reduce(0.0) {|z, word| z + (@@dic2[word] || 0)} > 0.0
    else
      false
    end
  end
  def self.normalize_train_data(body, maru)
    b1 = body.gsub(/[…]+/,"…").
      gsub(/[―]+/,"――").
      gsub(/[？！]/, "。").
      gsub(/[，]/, "、")
    if (maru)
      b1 = b1.gsub(/、/, '')
    else
      b1 = b1.gsub(/。/, '')
    end
    b1 = b1.gsub(/[\s　\r\n]/,"").
      gsub(/[\(（][^\)）]+[\)）]/, '').
      gsub(/^。/,'')
    b2 = b1.gsub(/「[^」]*」/, '').gsub(/[。]+/,'。').gsub(/[、]+/, '、')
    if (maru)
      b3 = (b2 + "。" + b1.scan(/「([^」]+)」/).map{|v|
              v
            }.join("。") + "。").gsub(/[。]+/,'。').gsub(/[、]+/, '、')
    else
      b2.gsub(/[。]+/,'。').gsub(/[、]+/, '、')
    end
  end

